package com.capstone.tempore.ui.homeimport androidx.lifecycle.ViewModelimport androidx.lifecycle.ViewModelProviderimport com.capstone.tempore.data.WeatherRepositoryimport com.capstone.tempore.ui.favorite.FavoriteViewModel@Suppress("UNCHECKED_CAST")class WeatherViewModelFactory(private val weatherRepository: WeatherRepository) : ViewModelProvider.Factory {    override fun <T : ViewModel> create(modelClass: Class<T>): T {        return when {            modelClass.isAssignableFrom(HomeViewModel::class.java) -> {                HomeViewModel(weatherRepository) as T            }            else -> throw IllegalArgumentException("Unknown ViewModel class: ${modelClass.name}")        }    }}