package com.capstone.temfore.diimport android.content.Contextimport com.capstone.temfore.data.UserRepositoryimport com.capstone.temfore.data.pref.UserPreferenceimport com.capstone.temfore.data.pref.dataStoreobject Injection {    fun provideRepository(context: Context): UserRepository {        val pref = UserPreference.getInstance(context.dataStore)        return UserRepository.getInstance(pref)    }}